document.addEventListener("DOMContentLoaded", function() {
    const table = document.getElementById("departmentTable");
    const emptyDataDiv = document.querySelector(".empty-data");
  
    // Check if there are no rows in the table body
    if (table.tBodies[0].rows.length === 0) {
        emptyDataDiv.style.display = "block";
    }
  });
  
  
  
  function filterTable(status, event) {
    const rows = document.querySelectorAll('.data-row');
    const noResultsMessage = document.getElementById('no-results');
    let found = false;
  
    rows.forEach(row => {
        const cell = row.querySelector('.status');
        if (cell) {
            if (status === 'All' || cell.textContent === status) {
                row.style.display = '';
                found = true;
            } else {
                row.style.display = 'none';
            }
        }
    });
  
    if (found) {
        noResultsMessage.style.display = 'none';
    } else {
        noResultsMessage.style.display = 'block';
    }
  
    // Update the active tab
    const buttons = document.querySelectorAll('.filter-tab button');
    buttons.forEach((button) => button.classList.remove('active'));
    event.target.classList.add('active');
  }

  // Add a click event listener to each radio input to update the styling on selection
  document.querySelectorAll('.setting-control-input').forEach(function(radio) {
    radio.addEventListener('change', function() {
      document.querySelectorAll('.setting-label').forEach(function(label) {
        label.classList.remove('selected');
      });
          document.querySelector('label[for="' + this.id + '"]').classList.add('selected');
    });
  });